// === LOAD THEME FUNCTION ===
document.addEventListener('DOMContentLoaded', () => {
  const urlParams = new URLSearchParams(window.location.search);
  const themeId = urlParams.get('id') || 'literature';
  loadTheme(themeId);
});

function loadTheme(themeId) {
  const theme = themesData[themeId];
  if (!theme) {
    console.error('Theme not found:', themeId);
    return;
  }

  document.title = `${theme.title} - –ñ–∏–≤–∞—Ç–∞ –∏—Å—Ç–æ—Ä–∏—è –Ω–∞ –ë—ä–ª–≥–∞—Ä–∏—è`;
  document.querySelector('.theme-icon-large').textContent = theme.icon;
  document.querySelector('.theme-title').textContent = theme.title;
  document.querySelector('.theme-period').textContent = theme.period;
  document.querySelector('.theme-tags').innerHTML = theme.tags.map(tag => `<span class="tag">${tag}</span>`).join('');
  document.querySelector('.theme-intro p').textContent = theme.intro;

  // Context
  const contextSection = document.querySelector('.theme-section');
  if (contextSection && theme.context) {
    contextSection.innerHTML = `<h2>–ò—Å—Ç–æ—Ä–∏—á–µ—Å–∫–∏ –∫–æ–Ω—Ç–µ–∫—Å—Ç</h2>${theme.context.map(p=>`<p>${p}</p>`).join('')}`;
  }

  // Highlights
  const highlightsGrid = document.querySelector('.highlights-grid');
  if (highlightsGrid && theme.highlights) {
    highlightsGrid.innerHTML = theme.highlights.map(h=>`
      <div class="highlight-card">
        <div class="highlight-icon">${h.icon}</div>
        <h3>${h.title}</h3>
        <p>${h.text}</p>
      </div>
    `).join('');
  }

  // Figures
  const figuresGrid = document.querySelector('.figures-grid');
  if (figuresGrid && theme.figures) {
    figuresGrid.innerHTML = theme.figures.map(f=>`
      <div class="figure-card">
        <div class="figure-info">
          <h3>${f.name}</h3>
          <p class="figure-role">${f.role}</p>
          <p class="figure-description">${f.description}</p>
        </div>
      </div>
    `).join('');
  }

  // Timeline
  const timelineContainer = document.querySelector('.theme-timeline');
  if (timelineContainer && theme.timeline) {
    timelineContainer.innerHTML = theme.timeline.map(t=>`
      <div class="timeline-event">
        <div class="timeline-marker"></div>
        <div class="timeline-year">${t.year}</div>
        <div class="timeline-content">
          <h4>${t.title}</h4>
          <p>${t.text}</p>
        </div>
      </div>
    `).join('');
  }

  // Legacy
  const legacyContent = document.querySelector('.legacy-content');
  if (legacyContent && theme.legacy) {
    legacyContent.innerHTML = theme.legacy.map(p=>`<p>${p}</p>`).join('');
  }

  // Quote
  const legacyQuote = document.querySelector('.legacy-quote');
  if (legacyQuote && theme.quote) {
    legacyQuote.innerHTML = `<blockquote>${theme.quote.text}</blockquote><cite>‚Äî ${theme.quote.author}</cite>`;
  }
}
